{
  "title": "Early Intervention Centre for Children with Cerebral Palsy in SRTC, Chennai and Tiruchirapalli Districts",
  "state": "Tamil Nadu",
  "department": "Department for the Welfare of Differently Abled Persons",
  "type": "Government Scheme",
  "beneficiary": "Children affected with cerebral palsy",
  "details": "The 'Early Intervention Centre for Children with Cerebral Palsy in SRTC, Chennai and Tiruchirapalli Districts' was launched by the Department for the Welfare of Differently Abled Persons, Tamil Nadu. Under this scheme, stimulation training is given to the children affected with cerebral palsy in the age group of below 6 years. The applications are accepted offline in the prescribed form available at the District Differently Abled Welfare Office.",
  "benefits": [
    "Early intervention provides stimulation training to the children."
  ],
  "eligibility": [
    "The young child/infants should be affected with cerebral palsy.",
    "The age of a young child/infant should be 6 years or below."
  ],
  "exclusions": [],
  "application_process": "Offline. Parents/guardians should visit the District Differently Abled Welfare Officer to request a hard copy of the application form, fill it out, and submit it along with the required documents.",
  "documents_required": [
    "National Identity Card of the Differently Abled.",
    "Birth Certificate.",
    "Any Other Document If Required."
  ],
  "faqs": [
    {
      "question": "What is the age limit for children eligible for the stimulation training program?",
      "answer": "The age limit for children eligible for the stimulation training program is below 6 years."
    },
    {
      "question": "How can parents apply for the program?",
      "answer": "Parents can apply for the program by submitting a prescribed application form."
    },
    {
      "question": "What documents are required to apply for the program?",
      "answer": "Documents required for application include a National Identity card for differently abled individuals and a Birth Certificate."
    },
    {
      "question": "Is there a fee for participating in the stimulation training?",
      "answer": "There is no fee for participating in the stimulation training program."
    },
    {
      "question": "Who oversees the administration of the program in Chennai and Tiruchirapalli districts?",
      "answer": "The administration of the program in Chennai and Tiruchirapalli districts is overseen by the District Differently Abled Welfare Officer."
    }
  ],
  "sources": [
    {
      "type": "Citizen's Charter",
      "url": "https://cms.tn.gov.in/sites/default/files/documents/wda_e_cc_2022_23.pdf"
    },
    {
      "type": "Scheme Details",
      "url": "https://www.myscheme.gov.in/schemes/eicccpsrtcctd"
    }
  ],
  "tags": [
    "Cerebral Palsy",
    "Children",
    "Differently Abled Person",
    "Infants"
  ]
}

{
  "title": "Shiksha Protsaahan Puraskaar yojana",
  "state": "Madhya Pradesh",
  "department": "Shram Kalyan Mandal",
  "type": "Financial Assistance",
  "beneficiary": "Children of registered workers of the Madhya Pradesh Shram Kalyan Mandal",
  "details": "The Shiksha Protsahan Puraskar Yojana is a scheme launched by the Madhya Pradesh government to provide financial assistance to the children of registered workers of the Madhya Pradesh Shram Kalyan Mandal for their higher education.",
  "benefits": [
    "General Graduate (B.A., B.Com, B.Sc., B.B.A.) : ₹10,000/-",
    "Engineering (B.E./B.Tech.) : ₹15,000/-",
    "M.B.B.S./ B.D.S. : ₹25,000/-",
    "Pharmacy, Ayurvedic, Homeopathic (U.G., P.G. Diploma) : ₹12,500/-",
    "Law Courses : ₹10,000/-",
    "General P.G. Courses (M.A, M.Com, M.Sc, MBA) : ₹10,000/-",
    "M.E/M.Tech : ₹11,500/-"
  ],
  "eligibility": [
    "The worker must be employed in an industrial unit or establishment located within the jurisdiction of the Madhya Pradesh Shram Kalyan Mandal.",
    "Up to two children of such workers who have studied in educational institutions of Madhya Pradesh are eligible for the award.",
    "The children must have secured at least 70% marks in their graduation, post-graduation, and BE examinations.",
    "For MBBS students, the minimum required marks is 60% in their previous year's examination."
  ],
  "exclusions": [],
  "application_process": "Offline",
  "documents_required": [
    "Original copy of the prescribed application form filled by the student (including certificate by employer, declaration form by student, and authentication by college).",
    "Photocopy of the self-attested mark sheet of the class passed by the applicant (previous year).",
    "Photocopy of the first page of the applicant's bank passbook clearly showing their name, bank account number, and IFSC code.",
    "Photocopy of the applicant's student/student's overall family card.",
    "Photocopy of the applicant student/student's Aadhar card.",
    "Photocopy of the applicant student/student's parents' Aadhar card."
  ],
  "faqs": [
    {
      "question": "What is the objective of the scheme?",
      "answer": "Under this scheme, an education incentive award amount is provided to the children of eligible workers studying in higher education who have passed at least first class in the previous class, on applying in the prescribed format."
    },
    {
      "question": "Who is eligible for the scheme?",
      "answer": "The children of eligible workers studying in higher education."
    },
    {
      "question": "May Other State persons also apply for this scheme?",
      "answer": "No, only for Madhya Pradesh."
    },
    {
      "question": "Is there a Caste-related Eligibility Criteria in this scheme?",
      "answer": "No."
    },
    {
      "question": "Is there any applications time limit under the scheme?",
      "answer": "Within 30 days from the publication of inviting applications under the scheme."
    },
    {
      "question": "Is there any Application fee?",
      "answer": "No."
    },
    {
      "question": "How many children from a same family can get the benefit?",
