  ]
}

{
  "title": "Cattle Shed Subsidy Scheme under Scheduled Castes Sub Plan (SC Category)",
  "state": "Gujarat",
  "department": "Animal Husbandry",
  "type": "Subsidy",
  "beneficiary": "Scheduled Caste",
  "details": "The scheme 'Cattle Shed Subsidy Scheme under Scheduled Castes Sub Plan (SCSP)' is implemented by the Directorate of Animal Husbandry, Agriculture, Farmers Welfare and Co-operation Department, Government of Gujarat. Under this scheme, the financial assistance is given to Scheduled Caste beneficiaries for construction of Cattle Shed for 2 animals.",
  "benefits": [
    "Under the scheme, financial assistance of either ₹30,000/- or 50% of the cost of the cattle shed, whichever is less, is provided to Scheduled Caste beneficiaries for the construction of a cattle shed for two animals."
  ],
  "eligibility": [
    "The beneficiary should be a permanent resident of Gujarat.",
    "The beneficiary should belong to the Scheduled Caste category.",
    "The beneficiary should possess the necessary land to his or her name or he/she should borrowed land on rent either up to ten year (10) or for more than ten year as well as necessary animals.",
    "Only those beneficiaries of scheduled castes keeping at least 2 (two) animals can only get the benefit of this scheme."
